import "./contact.scss";
import { useState } from "react";
<script src="https://smtpjs.com/v3/smtp.js"></script>


export default function Contact(){
    const[message, setMessage] = useState(false);

    function sendEmail(){
            Email.send({
                Host : "smtp.gmail.com",
                Username : "nicolaswester627@gmail.com",
                Password : "lMg79Cs6khwGU41r5BU1",
                To : 'nwester627@yahoo.com',
                From : document.getElementById("email").value,
                Subject : "New Contact Form Inquiry",
                Body : "Email: " + document.getElementById("email").value
                        +"<br> Message: " + document.getElementById("message").value
            }).then(
              message => alert("Message Sent Succesfully")
            )};

    const handleSubmit = (e) => {
        e.preventDefault();
        setMessage(true);
        sendEmail();
        reset();
        return(false);
    };
    return(
        <div className="contact" id="contact">
            <div className="left">
                <img src="https://raw.githubusercontent.com/safak/youtube/da700015c8a88050fad7b961e99a7039638ab3b2/public/assets/shake.svg" alt="" />
            </div>
            <div className="right">
                <h2>Contact.</h2>
                <form onSubmit={handleSubmit}>
                    <input type="text" id="email" placeholder="Email" required/>
                    <textarea type="text" id="message" placeholder="Message" required></textarea>
                    <button type="submit">Send</button>
                    {message && <span>Thanks, I'll reply soon! :)</span>}
                </form>
            </div>
            <script>
                    sendEmail();
            </script>
        </div>
    )
}
